import React, { useState } from 'react';
import axios from 'axios';

function PhotoForm() {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    tags: '',
    directory: '',
    image: null
  });

  const handleChange = e => {
    if (e.target.name === 'image') {
      setFormData({ ...formData, image: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleSubmit = e => {
    e.preventDefault();
    const data = new FormData();
    for (let key in formData) {
      data.append(key, formData[key]);
    }

    axios.post('http://127.0.0.1:8000/api/photos/', data)
      .then(res => {
        alert('Photo uploaded!');
      });
  };

  return (
    <form onSubmit={handleSubmit}>
  <input type="text" value={title} onChange={e => setTitle(e.target.value)} placeholder="Title" required />
  <textarea value={description} onChange={e => setDescription(e.target.value)} placeholder="Description" />
  <input type="text" value={tags} onChange={e => setTags(e.target.value)} placeholder="Tags" />
  <input type="text" value={directory} onChange={e => setDirectory(e.target.value)} placeholder="Directory" />
  <input type="file" onChange={e => setImage(e.target.files[0])} required />
  <button type="submit">Upload</button>
</form>

  );
}

export default PhotoForm;
