// components/PhotoList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';

function PhotoList() {
  const [photos, setPhotos] = useState([]);
  const [search, setSearch] = useState('');

  const fetchPhotos = () => {
    axios.get('http://127.0.0.1:8000/api/photos/')
      .then(res => {
        setPhotos(res.data);
      });
  };

  useEffect(() => {
    fetchPhotos();
  }, []);

  const filteredPhotos = photos.filter(photo =>
    photo.title.includes(search) ||
    photo.tags.includes(search) ||
    photo.description.includes(search)
  );

  const deletePhoto = id => {
    axios.delete(`http://127.0.0.1:8000/api/photos/${id}/`)
      .then(() => fetchPhotos());
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Search by tag, title, or description"
        onChange={e => setSearch(e.target.value)}
      />
      {filteredPhotos.map(photo => (
        <div key={photo.id}>
          <h3>{photo.title}</h3>
          <p>{photo.description}</p>
          <p><strong>Tags:</strong> {photo.tags}</p>
          <img
            src={`http://127.0.0.1:8000${photo.image}`}
            alt={photo.title}
            width="200"
          />
          <button onClick={() => deletePhoto(photo.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default PhotoList;
