import React, { useEffect, useState } from 'react';
import axios from 'axios';

function PhotoList() {
  const [photos, setPhotos] = useState([]);
  const [searchInput, setSearchInput] = useState('');
  const [filteredPhotos, setFilteredPhotos] = useState([]);

  const fetchPhotos = () => {
    axios.get('http://127.0.0.1:8000/api/photos/')
      .then(res => {
        setPhotos(res.data);
        setFilteredPhotos(res.data); // Show all initially
      })
      .catch(err => {
        console.error('Failed to fetch photos:', err);
      });
  };

  useEffect(() => {
    fetchPhotos();
  }, []);

  const handleSearch = () => {
    const filtered = photos.filter(photo =>
      photo.title.toLowerCase().includes(searchInput.toLowerCase()) ||
      photo.description.toLowerCase().includes(searchInput.toLowerCase()) ||
      photo.tags.toLowerCase().includes(searchInput.toLowerCase())
    );
    setFilteredPhotos(filtered);
  };

  const deletePhoto = id => {
    axios.delete(`http://127.0.0.1:8000/api/photos/${id}/`)
      .then(() => fetchPhotos());
  };

  return (
    <div>
      <div style={{ margin: '20px 0' }}>
        <input
          type="text"
          placeholder="Search by tag, title, or description"
          value={searchInput}
          onChange={e => setSearchInput(e.target.value)}
          style={{ padding: '8px', width: '300px', marginRight: '10px' }}
        />
        <button onClick={handleSearch} style={{ padding: '8px 16px' }}>
          Search
        </button>
      </div>

      {filteredPhotos.map(photo => (
        <div key={photo.id} style={{ border: '1px solid #ccc', marginBottom: '20px', padding: '10px' }}>
          <h3>{photo.title}</h3>
          <p><strong>Description:</strong> {photo.description}</p>
          <p><strong>Tags:</strong> {photo.tags}</p>
          <p><strong>Directory:</strong> {photo.directory}</p>
          <img
            src={`http://127.0.0.1:8000${photo.image}`}
            alt={photo.title}
            width="200"
            style={{ display: 'block', marginBottom: '10px' }}
          />
          <button onClick={() => deletePhoto(photo.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default PhotoList;
