// components/PhotoList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';

function PhotoList() {
  const [photos, setPhotos] = useState([]);
  const [search, setSearch] = useState('');

  // Fetch photos from backend
  const fetchPhotos = () => {
    axios.get('http://127.0.0.1:8000/api/photos/')
      .then(res => {
        setPhotos(res.data);
      })
      .catch(err => {
        console.error('Failed to fetch photos:', err);
      });
  };

  useEffect(() => {
    fetchPhotos();
  }, []);

  // Simple filter function based on title, tags, or description
  const filteredPhotos = photos.filter(photo =>
    photo.title.toLowerCase().includes(search.toLowerCase()) ||
    photo.description.toLowerCase().includes(search.toLowerCase()) ||
    photo.tags.toLowerCase().includes(search.toLowerCase())
  );

  const deletePhoto = id => {
    axios.delete(`http://127.0.0.1:8000/api/photos/${id}/`)
      .then(() => fetchPhotos());
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Search by tag, title, or description"
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ margin: '20px 0', padding: '8px', width: '300px' }}
      />

      {filteredPhotos.map(photo => (
        <div key={photo.id} style={{ border: '1px solid #ccc', marginBottom: '20px', padding: '10px' }}>
          <h3>{photo.title}</h3>
          <p><strong>Description:</strong> {photo.description}</p>
          <p><strong>Tags:</strong> {photo.tags}</p>
          <p><strong>Directory:</strong> {photo.directory}</p>
          <img
            src={`http://127.0.0.1:8000${photo.image}`}
            alt={photo.title}
            width="200"
            style={{ display: 'block', marginBottom: '10px' }}
          />
          <button onClick={() => deletePhoto(photo.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default PhotoList;
